#include <iostream>
#include "vector"
using namespace std;

vector<vector<int>> threeSum(vector<int> nums) {
    vector<vector<int>> result;
    sort(nums.begin(), nums.end());
    int n = (int) nums.size();
    for(int i=0; i<n; i++) {
        int l = i + 1, r = n - 1;
        while (l < r) {
            int sum = nums[l] + nums[r];
            int total = sum + nums[i];
            if (total == 0) {
                vector<int> elements = { nums[i], nums[l], nums[r] };
                result.push_back(elements);
                while (l<r && elements[1] == nums[l]) { l++; }
                while (l<r && elements[2] == nums[r]) { r--; }
            } else if (total < 0) {
                l++;
            } else {
                r--;
            }
        }
        while (i + 1 < nums.size() && nums[i + 1] == nums[i]) { i++; }
    }
    return result;
}

